mapper.update:
UPDATE employee
SET pw = #{pw, jdbcType=VARCHAR}
WHERE
emp_no = #{emp_no, jdbcType=VARCHAR}

UPDATE employee
SET nickname = #{nickname,
jdbcType=VARCHAR}
WHERE emp_no = #{emp_no, jdbcType=VARCHAR}

mapper.select:
select role_code
from employee
where emp_no = #{empNo}

SELECT * FROM employee WHERE emp_no = #{emp_no} AND name =
#{name} AND ssn =
#{ssn}

select * from ( select em.*, d.dept_title, r.role_title, row_number() over (order by em.name asc) as rown from employee em left join dept d on em.dept_code = d.dept_code left join role r on em.role_code = r.role_code where 1=1
and ( #{chosung} = '전체' or (#{chosung} = 'ㄱ' and em.name >= '가' and em.name < '나') or (#{chosung} = 'ㄴ' and em.name >= '나' and em.name < '다') or (#{chosung} = 'ㄷ' and em.name >= '다' and em.name < '라') or (#{chosung} = 'ㄹ' and em.name >= '라' and em.name < '마') or (#{chosung} = 'ㅁ' and em.name >= '마' and em.name < '바') or (#{chosung} = 'ㅂ' and em.name >= '바' and em.name < '사') or (#{chosung} = 'ㅅ' and em.name >= '사' and em.name < '아') or (#{chosung} = 'ㅇ' and em.name >= '아' and em.name < '자') or (#{chosung} = 'ㅈ' and em.name >= '자' and em.name < '차') or (#{chosung} = 'ㅊ' and em.name >= '차' and em.name < '카') or (#{chosung} = 'ㅋ' and em.name >= '카' and em.name < '타') or (#{chosung} = 'ㅌ' and em.name >= '타' and em.name < '파') or (#{chosung} = 'ㅍ' and em.name >= '파' and em.name < '하') or (#{chosung} = 'ㅎ' and em.name >= '하' and em.name < '힣') or (#{chosung} = 'a-z' and regexp_like(em.name, '^[A-Za-z]')) or (#{chosung} = '0-9' and regexp_like(em.name, '^[0-9]')) )
and d.dept_title = #{category}
and em.quit_yn = 'N' order by em.name ) where rown between #{startNum} and #{endNum}
